public class FindAnagrams {
    public String findAnagrams(String s, String p) {
        List<Integer> objects = new ArrayList<>();
        Integer left = 0;
        Integer rigth = 0;
        int start = 0;
        Integer minLen = Integer.MAX_VALUE;
        String res = s;
        Map<Character, Integer> window = new HashMap<>();
        Map<Character, Integer> need = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            need.put(p.charAt(i), need.getOrDefault(p.charAt(i), 0) + 1);
        }
        int match = 0;
        while (rigth < s.length()) {
            Character c = s.charAt(rigth);
            window.put(c, window.getOrDefault(c, 0) + 1);
            if (window.get(c) == need.get(c)) {
                match++;
            }
            rigth++;
            while (match == need.size()) {
                if (rigth - left < minLen) {
                    start = left;
                    minLen = rigth - left;
                }


                char c1 = s.charAt(left);
                if (window.size() < need.size()) {
                    match--;
                }
                left++;
            }
        }
        return minLen == Integer.MAX_VALUE ?
                "" : s.substring(start, minLen);

    }


    public  static List<Integer> findAnagrams2(String s, String p) {

        List<Integer> objects = new ArrayList<>();
        Integer left = 0;
        Integer rigth = 0;
        Map<Character, Integer> window = new HashMap<>();
        Map<Character, Integer> need = new HashMap<>();
        for (int i = 0; i < p.length(); i++) {
            need.put(p.charAt(i), need.getOrDefault(p.charAt(i), 0) + 1);
        }
        int match = 0;
        while (rigth < s.length()) {
            Character c = s.charAt(rigth);
            window.put(c, window.getOrDefault(c, 0) + 1);
            if (window.get(c).equals(need.get(c)) ) {
                match++;
            }

            rigth++;
            while (match == need.size()) {
                if (rigth - left == p.length()) {
                    objects.add(left);
                }
                Character c1 = s.charAt(left);
                if (need.containsKey(c1)) {
                    window.put(c1, window.getOrDefault(c1, 0) - 1);
                    if (window.get(c1) < need.get(c1)) {
                        match--;
                    }
                }
                left++;
            }
        }
        return objects;
    }

    public static void main(String[] args) {
        List<Integer> anagrams2 = findAnagrams2("", "");
        System.out.println(Arrays.toString(anagrams2.toArray() ) );

    }
}